contracts/CustomOpinionMaker.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract CustomOpinionMaker is Ownable {
    constructor(address initialOwner) Ownable(initialOwner) {}

    struct Execution {
        uint kernelId;
        bytes result;
        bytes proof;
        bool isValidated;
    }

    struct KernelResult {
        address addr;
        bool flag;
        string name;
        uint score;
    }

    // Function to get an opinion based on execution plans
    function getOpinion(bytes calldata executionPlan, uint kernelId)
        external
        view
        onlyOwner
        returns (bool opinion, bool isFinalized, bytes memory updatedPlan)
    {
        (opinion, isFinalized, updatedPlan) = _validateExecution(executionPlan, kernelId);
    }

    // Internal validation logic
    function _validateExecution(bytes calldata executionPlan, uint kernelId)
        private
        pure
        returns (bool opinion, bool isFinalized, bytes memory updatedPlan)
    {
        Execution[] memory executions = abi.decode(executionPlan, (Execution[]));

        for (uint i = 0; i < executions.length; i++) {
            Execution memory execution = executions[i];
            if (execution.kernelId == kernelId) {
                if (kernelId == 1) {
                    // Custom Criteria for Kernel 1
                    (bool success, uint value) = abi.decode(execution.result, (bool, uint));
                    (bool proofValid) = abi.decode(execution.proof, (bool));
                    if (success && value > 100 && proofValid) {
                        opinion = true;
                    } else {
                        isFinalized = true;
                    }
                } else if (kernelId == 2) {
                    // Custom Criteria for Kernel 2
                    (KernelResult memory res) = abi.decode(execution.result, (KernelResult));
                    (bool proofValid) = abi.decode(execution.proof, (bool));
                    if (res.flag && res.addr != address(0) && proofValid) {
                        opinion = true;
                    } else {
                        isFinalized = true;
                    }
                }

                if (i == executions.length - 1) {
                    isFinalized = true;
                }

                executions[i].isValidated = true;
                break;
            }
        }

        updatedPlan = abi.encode(executions);
    }
}

contracts/CustomTokenAuthority.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@oasisprotocol/sapphire-contracts/contracts/Sapphire.sol";
import "@oasisprotocol/sapphire-contracts/contracts/EthereumUtils.sol";

    // Constructor to set initial owner and initialize keys
contract CustomTokenAuthority is Ownable {
    struct Keypair {
        bytes pubKey;
        bytes privKey;
    }

    struct Execution {
        uint256 kernelId;
        bytes result;
        bytes proof;
        bool isValidated;
    }

    Keypair private signingKeypair;
    Keypair private accessKeypair;
    bytes32 private signingKeypairRetrievalPassword;
    address private opinionMaker;

    mapping(address => bool) private whitelist;
    mapping(bytes32 => bool) private runtimeDigests;
    mapping(uint256 => bool) private allowedKernels;

    constructor(address _initialOwner, address _opinionMaker) Ownable(_initialOwner) {
        require(_initialOwner != address(0), "Owner address cannot be zero");
        require(_opinionMaker != address(0), "Opinion maker address cannot be zero");
        opinionMaker = _opinionMaker;
    }


    // Keypair generation function
    function generateKey() external onlyOwner {
        bytes memory seed = Sapphire.randomBytes(32, "");
        (bytes memory pubKey, bytes memory privKey) = Sapphire.generateSigningKeyPair(
            Sapphire.SigningAlg.Secp256k1PrehashedKeccak256,
            seed
        );
        signingKeypair = Keypair(pubKey, privKey);
        accessKeypair = Keypair(pubKey, privKey); // Example - update appropriately
    }

    // Updates the opinion maker address
    function setOpinionMaker(address newOpinionMaker) external onlyOwner {
        require(newOpinionMaker != address(0), "Invalid opinion maker address");
        opinionMaker = newOpinionMaker;
    }

    // Allows the owner to update the signing keypair
    function setSigningKeypair(bytes calldata pubKey, bytes calldata privKey) external onlyOwner {
        signingKeypair = Keypair(pubKey, privKey);
    }

    // Sets a password for retrieving the private signing key
    function setSigningKeypairRetrievalPassword(string calldata password) external onlyOwner {
        signingKeypairRetrievalPassword = keccak256(abi.encodePacked(password));
    }

    // Returns the public key and its derived Ethereum address
    function getSigningKeypairPublicKey() external view returns (bytes memory, address) {
        address signingKeypairAddress = EthereumUtils.k256PubkeyToEthereumAddress(signingKeypair.pubKey);
        return (signingKeypair.pubKey, signingKeypairAddress);
    }

    // Returns the private key if the correct password is provided
    function getSigningKeypairPrivateKey(string calldata password) external view onlyOwner returns (bytes memory) {
        require(
            signingKeypairRetrievalPassword == keccak256(abi.encodePacked(password)),
            "Invalid password"
        );
        return signingKeypair.privKey;
    }

    // Adds or removes a kernel node from the whitelist
    function setWhitelist(address kernelNodePubKey, bool isAllowed) external onlyOwner {
        whitelist[kernelNodePubKey] = isAllowed;
    }

    // Updates the runtime digest state
    function setRuntimeDigest(bytes32 runtimeDigest, bool isAllowed) external onlyOwner {
        runtimeDigests[runtimeDigest] = isAllowed;
    }

    // Adds or removes an allowed kernel
    function setKernel(uint256 kernelId, bool isAllowed) external onlyOwner {
        allowedKernels[kernelId] = isAllowed;
    }

    // Checks if a kernel is allowed (no modifier needed)
    function isKernelAllowed(uint256 kernelId) external view returns (bool) {
            return allowedKernels[kernelId];
    }
    modifier onlyAllowedKernel(uint256 kernelId) {
        require(allowedKernels[kernelId], "Kernel is not allowed");
        _;
    }

    // Retrieves an opinion for a given kernel ID
 function getOpinion(uint256 kernelId)
    external
    view
    onlyAllowedKernel(kernelId)
    returns (bool, bool, bytes memory)
{
    (bool success, bytes memory data) = opinionMaker.staticcall(
        abi.encodeWithSignature("getOpinion(uint256)", kernelId)
    );
    require(success, "Opinion retrieval failed");

    (bool opinion, bool isFinalized, bytes memory updatedPlan) = abi.decode(data, (bool, bool, bytes));
    return (opinion, isFinalized, updatedPlan);
}


    // Signs kernel-related data and returns the results
    function sign(
        address senderAddress,
        bytes calldata /* executionPlan */,
        bytes calldata functionParams,
        bytes calldata kernelParamObjects,
        bytes calldata kernelResponses
    ) external view returns (bytes memory, bytes32, bytes memory, bytes32) {
        bytes32 functionParamsDigest = keccak256(abi.encode(functionParams));
        bytes32 kernelParamObjectsDigest = kernelParamObjects.length > 0
            ? keccak256(abi.encode(kernelParamObjects))
            : bytes32(0);
        bytes32 dataDigest = keccak256(
            abi.encode(functionParamsDigest, kernelParamObjectsDigest, senderAddress)
        );
        bytes32 kernelResponsesDigest = keccak256(abi.encode(kernelResponses, senderAddress));

        bytes memory kernelResponsesSignature = Sapphire.sign(
            Sapphire.SigningAlg.Secp256k1PrehashedKeccak256,
            abi.encodePacked(signingKeypair.privKey),
            abi.encode(kernelResponsesDigest),
            ""
        );

        bytes32 nonce = bytes32(Sapphire.randomBytes(32, ""));
        bytes memory signatureToken = Sapphire.sign(
            Sapphire.SigningAlg.Secp256k1PrehashedKeccak256,
            abi.encodePacked(signingKeypair.privKey),
            abi.encode(dataDigest, nonce),
            ""
        );

        return (kernelResponsesSignature, kernelResponsesDigest, signatureToken, nonce);
    }
}

contracts/KRNL.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract KRNLTestToken is ERC20, Ownable {
    mapping(address => bool) public whitelist_;
    mapping(address => bool) public claimed;
    bool public distributionStarted_;

    event Whitelisted(address indexed _address);
    event TokensClaimed(address indexed _address, uint256 amount);
    event DistributionStarted();
    event DistributionStopped();

    // Specify custom decimals here
    uint8 private constant CUSTOM_DECIMALS = 0;

    constructor() ERC20("KRNL Test Token", "KRNL") Ownable(msg.sender) {
        _mint(msg.sender, 1000000); // Mint the total supply to the contract owner
    }

    function decimals() public view virtual override returns (uint8) {
        return CUSTOM_DECIMALS;
    }

    function addToWhitelist(address _address) public onlyOwner {
        whitelist_[_address] = true;
        emit Whitelisted(_address);
    }

    function addToWhitelistBatch(address[] calldata _addresses) public onlyOwner {
        for (uint256 i = 0; i < _addresses.length; i++) {
            whitelist_[_addresses[i]] = true;
            emit Whitelisted(_addresses[i]);
        }
    }

    function startDistribution() public onlyOwner {
        distributionStarted_ = true;
        emit DistributionStarted();
    }

    function stopDistribution() public onlyOwner {
        distributionStarted_ = false;
        emit DistributionStopped();
    }

    function claimTokens() public {
        require(distributionStarted_, "Distribution not started");
        require(whitelist_[msg.sender], "Not on whitelist");
        require(!claimed[msg.sender], "Tokens already claimed");

        claimed[msg.sender] = true;
        _transfer(owner(), msg.sender, 1000); // Transfer 1000 tokens to the caller
        emit TokensClaimed(msg.sender, 1000);
    }
}

contracts/KrnlRegistered.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {MessageHashUtils} from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

// Struct to group the parameters
struct KrnlPayload {
    bytes auth;
    bytes kernelResponses;
    bytes kernelParamObjects;
}

// Draft Version
contract KrnlRegistered is Ownable {
    error UnauthorizedTransaction();

    address public tokenAuthorityPublicKey;
    mapping(bytes => bool) public executed;

    modifier onlyAuthorized(KrnlPayload memory krnlPayload, bytes memory params) {
        if (!_isAuthorized(krnlPayload, params)) {
            revert UnauthorizedTransaction();
        }

        _;
    }

    constructor(address _tokenAuthorityPublicKey) Ownable(msg.sender) {
        tokenAuthorityPublicKey = _tokenAuthorityPublicKey;
    }

    function setTokenAuthorityPublicKey(address _tokenAuthorityPublicKey) external onlyOwner {
        tokenAuthorityPublicKey = _tokenAuthorityPublicKey;
    }

    function _isAuthorized(KrnlPayload memory krnlPayload, bytes memory params) private returns (bool) {
        (
            bytes memory kernelResponsesSignature,
            bytes32 kernelParamObjectsDigest,
            bytes memory signatureToken,
            bytes32 nonce
        ) = abi.decode(krnlPayload.auth, (bytes, bytes32, bytes, bytes32));

        if (executed[signatureToken]) {
            return false;
        }

        bytes32 kernelResponsesDigest = keccak256(abi.encode(krnlPayload.kernelResponses, msg.sender));
        bytes32 ethSignedKernelResponsesDigest = MessageHashUtils.toEthSignedMessageHash(kernelResponsesDigest);
        address recoveredAddress = ECDSA.recover(ethSignedKernelResponsesDigest, kernelResponsesSignature);
        if (recoveredAddress != tokenAuthorityPublicKey) {
            return false;
        }

        bytes32 calculatedKernelParamObjectsDigest = keccak256(abi.encode(krnlPayload.kernelParamObjects, msg.sender));
        if (calculatedKernelParamObjectsDigest != kernelParamObjectsDigest) {
            return false;
        }

        bytes32 functionParamsDigest = keccak256(abi.encode(params));

        bytes32 dataDigest = keccak256(abi.encode(functionParamsDigest, kernelParamObjectsDigest, msg.sender, nonce));
        bytes32 ethSignedDataDigest = MessageHashUtils.toEthSignedMessageHash(dataDigest);
        recoveredAddress = ECDSA.recover(ethSignedDataDigest, signatureToken);
        if (recoveredAddress != tokenAuthorityPublicKey) {
            return false;
        }

        executed[signatureToken] = true;
        return true;
    }
}

